/* jshint esversion:6, node:true, loopfunc:true, undef: true, unused: true, sub:true */
"use strict";
var iMersenne = 2147483647;

function Prng(seed) {
  this.seed = seed || 123;
}

Prng.prototype.rnd = function() {
  this.seed = this.seed*16807%iMersenne;
  return this.seed;
};

Prng.prototype.random = function() {
  return this.rnd()/iMersenne;
};

module.exports = Prng;
